using System;using System.Collections.Generic;using System.Linq;using System.Text;using FacilitadorDeEscala.Models;namespace FacilitadorDeEscala.Services{public class EscalaService{public List<int>GerarSequenciaDias(int totalDias){var d=new List<int>();int fim=totalDias==31?31:30;for(int i=16;i<=fim;i++)d.Add(i);for(int i=1;i<=15;i++)d.Add(i);return d;}public string GerarCsv(int t,List<Funcionario>fs){var dias=GerarSequenciaDias(t);var sb=new StringBuilder();sb.Append("Nome;");sb.AppendLine(string.Join(";",dias));foreach(var f in fs){sb.Append(f.Nome+";");for(int i=0;i<dias.Count;i++)sb.Append(f.FolgasNaEscala.Length>i&&f.FolgasNaEscala[i]?"F":"-").Append(";");sb.AppendLine();}return sb.ToString();}}}